GaussPattersonQuadrature::GaussPattersonQuadrature() : Quadrature(1){}




unsigned int GaussPattersonQuadrature::Exactness(unsigned int index) const {

  switch (index) {
    case 0: return 1;
    case 1: return 5;
    case 2: return 10;
    case 3: return 22;
    case 4: return 46;
    case 5: return 190;
    case 6: return 382;
    case 7: return 766;
    default:
    {
      std::stringstream msg;
      msg << "In GaussPattersonQuadrature::Exactness.  Asked for exactness of index " << index;
      msg << " but the maximum allowed index is 7.  The index 7 rule has 255 points ";
      msg << "and a polynomial accuracy of 766.  Do you need more than that?";
      throw std::runtime_error(msg.str());
      return 0;
    }
  }
}

